        $(".tab_js_asian a").removeClass('active')
        $(this).addClass('active');
        let id_tab_content = $(this).attr('href');
        $('.ldp-section2 .wrap_sec2 .bottom').hide();
        $(id_tab_content).fadeIn();
        return false;
    });


});
</script>